const essentials = require("../modules/essentials.js");
exports.catalogSearch = function(keyword, resultsPerPage, callback) {
    if((!keyword) || (!resultsPerPage) || (!callback)) throw new Error("You are missing arguments from catalogSearch, please refer to the documentation!");
    essentials.requestURL(`http://search.roblox.com/catalog/json?Keyword=${keyword}&ResultsPerPage=${resultsPerPage}&SortType=0`).then(function(promise) {
        const parsedBody = JSON.parse(promise);
        callback(parsedBody);
    });
};