const essentials = require("../modules/essentials.js");
exports.avatarThumbnail = function(id, width, height, callback) {
    if((!id) || (!callback) || (!width) || (!height)) throw new Error("You are missing arguments from avatarThumbnail, please refer to the documentation!");
    essentials.requestURL(`https://www.roblox.com/avatar-thumbnail/json?width=${width}&height=${height}&userId=${id}`).then(function(promise) {
        const parsedBody = JSON.parse(promise);
        callback(parsedBody);
    });
}

