const essentials = require("../modules/essentials.js");
exports.checkIfUserOwnsAsset = function(userID, assetID, callback) {
    if((!userID) || (!assetID) || (!callback)) throw new Error("You are missing arguments from checkIfUserOwnsAsset, please refer to the documentation!");
    essentials.checkUserExistance(userID);
    essentials.requestURL(`https://api.roblox.com/Ownership/HasAsset?userId=${userID}&assetId=${assetID}`).then(function(promise) {
        callback(promise); // Returns the boolean (not in json just plain text).
    });
};

exports.getPlacesFromID = function(id, callback) {
  if((!id) || (!callback)) throw new Error("You are missing arguments from getBestFriendsFromID, please refer to the documentation!");
  essentials.checkUserExistance(id);
  essentials.requestURL(`https://www.roblox.com/Contests/Handlers/Showcases.ashx?userId=${id}`).then(function(promise) {
    const parsedBody = JSON.parse(promise.body);
    callback(parsedBody.Showcase); //Returns an array of objects about the showcases.
  });
};